import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import DietForm from "../components/DietForm";
import { generateDietPlan, getDietPlan } from "../services/api";
import styles from "./DietPlan.module.css";

const DietPage = () => {
  const token = localStorage.getItem("token") || "";
  const dispatch = useDispatch();
  const [loading, setLoading] = useState(false);
  const [dietGenerated, setDietGenerated] = useState(false);

  const dietPlan = useSelector((state) => state.diet.dietPlan);

  // ✅ Fetch Existing Diet Plan
  const handleFetchDietPlan = () => {
    setLoading(true);
    getDietPlan(token)
      .then((data) => {
        if (data) {
          setDietGenerated(true);
        }
      })
      .finally(() => setLoading(false));
  };

  // ✅ Generate New Diet Plan
  const handleGenerateDietPlan = () => {
    setLoading(true);
    generateDietPlan({}, token)
      .then((data) => {
        if (data) {
          setDietGenerated(true);
        }
      })
      .finally(() => setLoading(false));
  };

  return (
    <div className={styles.dietPageContainer}>
      <h1 className={styles.pageTitle}>Your Personalized Diet Plan</h1>

      <div className={styles.buttonContainer}>
        {!dietGenerated && (
          <button onClick={handleGenerateDietPlan} className={styles.generateButton}>
            Generate Diet Plan
          </button>
        )}
        <button onClick={handleFetchDietPlan} className={styles.fetchButton}>
          Load Diet Plan
        </button>
      </div>

      {loading && <p className={styles.loadingText}>Loading...</p>}

      {!loading && dietPlan && dietGenerated && <DietForm />}
    </div>
  );
};

export default DietPage;
