import React, { useEffect, useState } from 'react';
import styles from './DietPlanForm.module.css';
import Spinner from './Spinner';
import DietPlan from './DietPlan';

const DietForm = ({ fetchSavedUserData, fetchDietPlan }) => {
  const [userData, setUserData] = useState(null);
  const [dietPlan, setDietPlan] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    // Fetch saved user settings from the database
    fetchSavedUserData()
      .then((data) => {
        setUserData(data);
        return fetchDietPlan(data); // Fetch the diet plan using stored user data
      })
      .then((plan) => setDietPlan(parseDietPlan(plan)))
      .finally(() => setLoading(false));
  }, []);

  const parseDietPlan = (dietPlanText) => {
    return dietPlanText
      .split('\n\n')
      .map((dayText) => {
        const [dayName, ...mealLines] = dayText.trim().split('\n');
        const meals = mealLines.map((line) => {
          const [mealType, description] = line.split(' - ').map((str) => str.trim());
          return { meal: mealType, description };
        });
        return { day: dayName, meals };
      });
  };

  return (
    <div className={styles.dietPlanFormPage}>
      {loading && (
        <div className={styles.spinnerContainer}>
          <Spinner />
        </div>
      )}

      {!loading && dietPlan ? (
        <DietPlan dietPlan={dietPlan} />
      ) : (
        <p className={styles.noPlanMessage}>No diet plan available. Please update your settings.</p>
      )}
    </div>
  );
};

export default DietForm;
