import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUserProfile, updateUserProfile } from '../store/actions/userActions';

const ProfilePage = () => {
  const token = localStorage.getItem('token') || '';
  const dispatch = useDispatch();

  const userData = useSelector((state) => state.user.userData);
  const loading = useSelector((state) => state.user.loading);

  const [formData, setFormData] = useState({
    weight: '',
    height: '',
    age: '',
    gender: '',
    favoriteFoods: '',
    dislikedFoods: '',
    typeOfWorkout: '',
    days: '',
  });

  useEffect(() => {
    if (token) {
      dispatch(fetchUserProfile(token));
    }
  }, [dispatch, token]);

  useEffect(() => {
    if (userData) {
      setFormData(userData);
    }
  }, [userData]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch(updateUserProfile(formData, token));
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Profile Settings</h2>

      {loading && <p>Loading...</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Weight:</label>
          <input type="text" name="weight" value={formData.weight} onChange={handleChange} />
        </div>
        <div>
          <label>Height:</label>
          <input type="text" name="height" value={formData.height} onChange={handleChange} />
        </div>
        <div>
          <label>Age:</label>
          <input type="text" name="age" value={formData.age} onChange={handleChange} />
        </div>
        <div>
          <label>Gender:</label>
          <input type="text" name="gender" value={formData.gender} onChange={handleChange} />
        </div>
        <div>
          <label>Favorite Foods:</label>
          <input type="text" name="favoriteFoods" value={formData.favoriteFoods} onChange={handleChange} />
        </div>
        <div>
          <label>Disliked Foods:</label>
          <input type="text" name="dislikedFoods" value={formData.dislikedFoods} onChange={handleChange} />
        </div>
        <div>
          <label>Type of Workout:</label>
          <input type="text" name="typeOfWorkout" value={formData.typeOfWorkout} onChange={handleChange} />
        </div>
        <div>
          <label>Days:</label>
          <input type="text" name="days" value={formData.days} onChange={handleChange} />
        </div>
        <button type="submit" style={{ marginTop: '1rem' }}>Save Profile</button>
      </form>
    </div>
  );
};

// ✅ Ensure ProfilePage is exported as default
export default ProfilePage;
